import { query, get, removeAddClass } from "../../load.min.js";
import { base_url, base_root } from "../../load.min.js";

const p = ".btn-profile",
    u = ".btn-location",
    ac = "btn-option-active";

query(p).addEventListener("click", e => {
    removeAddClass(p, u, ac);
    get(base_root + "/Admin_user", ".cnt-user-add");
});

query(u).addEventListener("click", e => {
    removeAddClass(u, p, ac);
    get(base_root + "/ubicacion", ".cnt-user-add");
});

query(".bt-cerrar").addEventListener("click", e => {
    get(base_root + "/cerrarsesion", "", function(res) {
        window.location.href = base_root;
    });
});