import { query, queryAll, load_on } from "../../../load.min.js";
import { Create, Edit, Delete, Buscar, ObtenerEstado } from "../axios.min.js";

load_on(document, 'click', '.btn-agregar-modal', function(e) {
    let data = {
        ADM_CodigoInterno: 0,
        USU_CodigoInterno: query("#USU_CodigoInterno").value,
        ADM_Write: ObtenerEstado(".ADM_Write"),
        ADM_READ: ObtenerEstado(".ADM_READ"),
        ADM_VIEW: ObtenerEstado(".ADM_VIEW"),
        ADM_Estado: "1",
    };


    Create(data);
});


load_on(document, 'click', '.btn-crud-edit', function(e) {
    let codigo = query('#CodigoInterno') != "" ? query('#CodigoInterno').value : 0;

    let data = {
        ADM_CodigoInterno: codigo,
        USU_CodigoInterno: query("#USU_CodigoInterno").value,
        ADM_Write: ObtenerEstado(".ADM_Write"),
        ADM_READ: ObtenerEstado(".ADM_READ"),
        ADM_VIEW: ObtenerEstado(".ADM_VIEW"),
        ADM_Estado: "1",
    };

    Edit(codigo, data);
});

load_on(document, 'click', '.btn-filter-guardar', function(e) {
    let id = query('.general-id') != null ? query('.general-id').getAttribute("data-id") : 0;
    let data = {
        ADM_CodigoInterno: id,
        USU_CodigoInterno: "",
        ADM_Write: "",
        ADM_READ: "",
        ADM_VIEW: "",
        ADM_Estado: "1",
    };

    Delete(id, data);
});

load_on(document, 'click', '.btn-crud-Agregar', function(e) {
    let data = {
        ADM_CodigoInterno: query("#Bus_ADM_CodigoInterno").value == "" ? "%%" : query("#Bus_ADM_CodigoInterno").value,
        USU_CodigoInterno: query("#Bus_USU_CodigoInterno").value == "" ? "%%" : query("#Bus_USU_CodigoInterno").value,
        ADM_Write: query("#Bus_ADM_Write").value == "" ? "%%" : query("#Bus_ADM_Write").value,
        ADM_READ: query("#Bus_ADM_READ").value == "" ? "%%" : query("#Bus_ADM_READ").value,
        ADM_VIEW: query("#Bus_ADM_VIEW").value == "" ? "%%" : query("#Bus_ADM_VIEW").value,
        ADM_Estado: query("#Bus_ADM_Estado").value == "" ? "%%" : query("#Bus_ADM_Estado").value,
    };

    Buscar(data);
});
