import { query, queryAll, load_on } from "../../../load.min.js";
import { Create, Edit, Delete, Buscar } from "../axios.min.js";

load_on(document, 'click', '.btn-agregar-modal', function(e) {
    let data = {
        BAN_CodigoInterno: 0,
        BAN_Imagen: query("#BAN_Imagen").value,
        BAN_Ruta: query("#BAN_Ruta").value,
        TIPBAN_CodigoInterno: query("#TIPBAN_CodigoInterno").value,
        PAQ_CodigoInterno: query("#PAQ_CodigoInterno").value,
        BAN_Estado: "1",
    };


    Create(data);
});


load_on(document, 'click', '.btn-crud-edit', function(e) {
    let codigo = query('#CodigoInterno') != "" ? query('#CodigoInterno').value : 0;

    let data = {
        BAN_CodigoInterno: codigo,
        BAN_Imagen: query("#BAN_Imagen").value,
        BAN_Ruta: query("#BAN_Ruta").value,
        TIPBAN_CodigoInterno: query("#TIPBAN_CodigoInterno").value,
        PAQ_CodigoInterno: query("#PAQ_CodigoInterno").value,
        BAN_Estado: "1",
    };

    Edit(codigo, data);
});

load_on(document, 'click', '.btn-filter-guardar', function(e) {
    let id = query('.general-id') != null ? query('.general-id').getAttribute("data-id") : 0;
    let data = {
        BAN_CodigoInterno: id,
        BAN_Imagen: "",
        BAN_Ruta: "",
        TIPBAN_CodigoInterno: "",
        PAQ_CodigoInterno: "",
        BAN_Estado: "1",
    };

    Delete(id, data);
});

load_on(document, 'click', '.btn-crud-Agregar', function(e) {
    let data = {
        BAN_CodigoInterno: query("#Bus_BAN_CodigoInterno").value == "" ? "%%" : query("#Bus_BAN_CodigoInterno").value,
        TIPBAN_CodigoInterno: query("#Bus_TIPBAN_CodigoInterno").value == "" ? "%%" : query("#Bus_TIPBAN_CodigoInterno").value,
        PAQ_CodigoInterno: query("#Bus_PAQ_CodigoInterno").value == "" ? "%%" : query("#Bus_PAQ_CodigoInterno").value,
        BAN_Estado: query("#Bus_BAN_Estado").value == "" ? "%%" : query("#Bus_BAN_Estado").value,
    };

    Buscar(data);
});
