import { query, queryAll, load_on } from "../../../load.min.js";
import { Create, Edit, Delete, Buscar } from "../axios.min.js";

load_on(document, 'click', '.btn-agregar-modal', function(e) {
    let data = {
        CKM_CodigoInterno: 0,
        CKM_Precio: query("#CKM_Precio").value,
        CKM_Metros: query("#CKM_Metros").value,
        CKM_estado: '1',
    };

    Create(data);
});


load_on(document, 'click', '.btn-crud-edit', function(e) {
    let codigo = query('#CodigoInterno') != "" ? query('#CodigoInterno').value : 0;

    let data = {
        CKM_CodigoInterno: codigo,
        CKM_Precio: query("#CKM_Precio").value,
        CKM_Metros: query("#CKM_Metros").value,
        CKM_estado: '1',
    };

    Edit(codigo, data);
});

load_on(document, 'click', '.btn-filter-guardar', function(e) {
    let id = query('.general-id') != null ? query('.general-id').getAttribute("data-id") : 0;
    let data = {
        CKM_CodigoInterno: id,
        CKM_Precio: "",
        CKM_Metros: "",
        CKM_estado: '1',
    };

    Delete(id, data);
});

load_on(document, 'click', '.btn-crud-Agregar', function(e) {
    let data = {
        CKM_CodigoInterno: query("#Bus_CKM_CodigoInterno").value == "" ? "%%" : query("#Bus_CKM_CodigoInterno").value,
        CKM_Precio: query("#Bus_CKM_Precio").value == "" ? "%%" : query("#Bus_CKM_Precio").value,
        CKM_Metros: query("#Bus_CKM_Metros").value == "" ? "%%" : query("#Bus_CKM_Metros").value,
        CKM_estado: query("#Bus_CKM_Estado").value == "" ? "%%" : query("#Bus_CKM_Estado").value,
    };

    Buscar(data);
});
