load_on(document, 'click', '.btn-crud-edit', function(e) {
    let codigo = query('#CodigoInterno') != "" ? query('#CodigoInterno').value : 0;
    let data = {
        DIS_Codigo: codigo,
        DIS_Nombre: query("#DIS_Nombre").value,
        DIS_Estado: '1',
    };

    Edit(codigo, data);
});

load_on(document, 'click', '.btn-crud-Agregar', function(e) {
    let data = {
        DIS_Codigo: query("#Bus_DIS_Codigo").value == "" ? "%%" : query("#Bus_DIS_Codigo").value,
        DIS_Nombre: query("#Bus_DIS_Nombre").value == "" ? "%%" : query("#Bus_DIS_Nombre").value,
        DIS_Estado: query("#Bus_DIS_Estado").value == "" ? "%%" : query("#Bus_DIS_Estado").value,
    };

    Buscar(data);
});


load_on(document, 'click', '.btn-filter-guardar', function(e) {
    let id = query('.general-id') != null ? query('.general-id').getAttribute("data-id") : 0;
    let data = {
        DIS_Codigo: id,
        DIS_Nombre: "",
        DIS_Estado: '0',
    };

    Delete(id, data);
});
