load_on(document, 'click', '.btn-agregar-modal', function(e) {
    let data = {
        FORM_CodigoInterno: 0,
        FORM_Nombre: query("#FORM_Nombre").value,
        FORM_Identificador: query("#FORM_Identificador").value,
        FORM_Estado: '1',
    };


    Create(data);
});


load_on(document, 'click', '.btn-crud-edit', function(e) {
    let codigo = query('#CodigoInterno') != "" ? query('#CodigoInterno').value : 0;

    let data = {
        FORM_CodigoInterno: codigo,
        FORM_Nombre: query("#FORM_Nombre").value,
        FORM_Identificador: query("#FORM_Identificador").value,
        FORM_Estado: '1',
    };

    Edit(codigo, data);
});

load_on(document, 'click', '.btn-filter-guardar', function(e) {
    let id = query('.general-id') != null ? query('.general-id').getAttribute("data-id") : 0;
    let data = {
        FORM_CodigoInterno: id,
        FORM_Nombre: "",
        FORM_Identificador: "",
        FORM_Estado: '1',
    };

    Delete(id, data);
});

load_on(document, 'click', '.btn-crud-Agregar', function(e) {
    let data = {
        FORM_CodigoInterno: query("#Bus_FORM_CodigoInterno").value == "" ? "%%" : query("#Bus_FORM_CodigoInterno").value,
        FORM_Nombre: query("#Bus_FORM_Nombre").value == "" ? "%%" : query("#Bus_FORM_Nombre").value,
        FORM_Identificador: query("#Bus_FORM_Identificador").value == "" ? "%%" : query("#Bus_FORM_Identificador").value,
        FORM_Estado: query("#Bus_FORM_Estado").value == "" ? "%%" : query("#Bus_FORM_Estado").value,
    };

    Buscar(data);
});
