import { query, queryAll, load_on } from "../../../load.min.js";
import { Create, Edit, Delete, Buscar } from "../axios.min.js";

load_on(document, 'click', '.btn-agregar-modal', function(e) {
    let data = {
        ITEM_CodigoInterno: 0,
        ITEM_Descripcion: query("#ITEM_Descripcion").value,
        ITEM_Precio: query("#ITEM_Precio").value,
        TIPIT_CodigoInterno: query("#TIPIT_CodigoInterno").getAttribute("data-id"),
        ITEM_Estado: '1',
    };

    Create(data);
});

load_on(document, 'click', '.btn-crud-edit', function(e) {
    let codigo = query('#CodigoInterno') != "" ? query('#CodigoInterno').value : 0;

    let data = {
        ITEM_CodigoInterno: codigo,
        ITEM_Descripcion: query("#ITEM_Descripcion").value,
        ITEM_Precio: query("#ITEM_Precio").value,
        TIPIT_CodigoInterno: query("#TIPIT_CodigoInterno").getAttribute("data-id"),
        ITEM_Estado: '1',
    };

    Edit(codigo, data);
});

load_on(document, 'click', '.btn-filter-guardar', function(e) {
    let id = query('.general-id') != "" ? query('.general-id').getAttribute("data-id") : 0;
    let data = {
        ITEM_CodigoInterno: id,
        ITEM_Descripcion: "",
        ITEM_Precio: "0",
        TIPIT_CodigoInterno: "",
        ITEM_Estado: '0',
    };

    Delete(id, data);
});

load_on(document, 'click', '.btn-crud-Agregar', function(e) {
    let data = {
        ITEM_CodigoInternoInterno: query("#Bus_ITEM_CodigoInterno").value == "" ? "%%" : query("#Bus_ITEM_CodigoInterno").value,
        ITEM_Precio: "%%",
        TIPIT_CodigoInterno: query("#BUS_TIPIT_Buscar").value == "" ? "%%" : query("#Bus_TIPIT_Buscar").value,
        ITEM_Estado: query("#Bus_ITEM_Estado").value == "" ? "%%" : query("#Bus_ITEM_Estado").value,
    };

    Buscar(data);
});