import { query, queryAll, load_on } from "../../../load.min.js";
import { Create, Edit, Delete, Buscar } from "../axios.min.js";

load_on(document, 'click', '.btn-agregar-modal', function(e) {
    let data = {
        MEN_CodigoInterno: 0,
        MEN_CodigoPadre: query("#MEN_CodigoPadre").value,
        MEN_Nombre: query("#MEN_Nombre").value,
        MEN_Tipo: query("#MEN_Tipo").value,
        MEN_Icono: query("#MEN_Icono").value,
        MEN_Ruta: query("#MEN_Ruta").value,
        MEN_Estado: "1",
    };


    Create(data);
});


load_on(document, 'click', '.btn-crud-edit', function(e) {
    let codigo = query('#CodigoInterno') != "" ? query('#CodigoInterno').value : 0;

    let data = {
        MEN_CodigoInterno: codigo,
        MEN_CodigoPadre: query("#MEN_CodigoPadre").value,
        MEN_Nombre: query("#MEN_Nombre").value,
        MEN_Tipo: query("#MEN_Tipo").value,
        MEN_Icono: query("#MEN_Icono").value,
        MEN_Ruta: query("#MEN_Ruta").value,
        MEN_Estado: "1",
    };

    Edit(codigo, data);
});

load_on(document, 'click', '.btn-filter-guardar', function(e) {
    let id = query('.general-id') != null ? query('.general-id').getAttribute("data-id") : 0;
    let data = {
        MEN_CodigoInterno: id,
        MEN_CodigoPadre: "",
        MEN_Nombre: "",
        MEN_Tipo: "",
        MEN_Icono: "",
        MEN_Ruta: "",
        MEN_Estado: "1",
    };

    Delete(id, data);
});

load_on(document, 'click', '.btn-crud-Agregar', function(e) {
    let data = {
        MEN_CodigoInterno: query("#Bus_MEN_CodigoInterno").value == "" ? "%%" : query("#Bus_MEN_CodigoInterno").value,
        MEN_CodigoPadre: query("#Bus_MEN_CodigoPadre").value == "" ? "%%" : query("#Bus_MEN_CodigoPadre").value,
        MEN_Nombre: query("#Bus_MEN_Nombre").value == "" ? "%%" : query("#Bus_MEN_Nombre").value,
        MEN_Tipo: query("#Bus_MEN_Tipo").value == "" ? "%%" : query("#Bus_MEN_Tipo").value,
        MEN_Estado: query("#Bus_MEN_Estado").value == "" ? "%%" : query("#Bus_MEN_Estado").value,
    };

    Buscar(data);
});
