import { query, queryAll, load_on } from "../../../load.min.js";
import { Create, Edit, Delete, Buscar } from "../axios.min.js";

load_on(document, 'click', '.btn-agregar-modal', function(e) {
    let data = {
        PROV_Codigo: 0,
        PROV_Nombre: query("#PROV_Nombre").value,
        PROV_Estado: '1',
    };

    Create(data);
});

load_on(document, 'click', '.btn-crud-edit', function(e) {
    let codigo = query('#CodigoInterno') != "" ? query('#CodigoInterno').value : 0;
    let data = {
        PROV_Codigo: codigo,
        PROV_Nombre: query("#PROV_Nombre").value,
        PROV_Estado: '1',
    };

    Edit(codigo, data);
});

load_on(document, 'click', '.btn-filter-guardar', function(e) {
    let id = query('.general-id') != null ? query('.general-id').getAttribute("data-id") : 0;
    let data = {
        PROV_Codigo: id,
        PROV_Nombre: "",
        PROV_Estado: '0',
    };

    Delete(id, data);
});

load_on(document, 'click', '.btn-crud-Agregar', function(e) {
    let data = {
        PROV_Codigo: query("#Bus_PROV_Codigo").value == "" ? "%%" : query("#Bus_PROV_Codigo").value,
        PROV_Nombre: query("#Bus_PROV_Nombre").value == "" ? "%%" : query("#Bus_PROV_Nombre").value,
        PROV_Estado: query("#Bus_PROV_Estado").value == "" ? "%%" : query("#Bus_PROV_Estado").value,
    };

    Buscar(data);
});
