import { query, queryAll, load_on } from "../../../load.min.js";
import { Create, Edit, Delete, Buscar } from "../axios.min.js";

load_on(document, 'click', '.btn-agregar-modal', function(e) {

    let data = {
        SEX_Codigo: 0,
        SEX_Nombre: query("#SEX_Nombre").value,
        SEX_Identificador: query("#SEX_Identificador").value,
        SEX_Estado: '1',
    };

    Create(data);
});

load_on(document, 'click', '.btn-crud-edit', function(e) {
    let codigo = query('#CodigoInterno') != "" ? query('#CodigoInterno').value : 0;
    let data = {
        SEX_Codigo: codigo,
        SEX_Nombre: query("#SEX_Nombre").value,
        SEX_Identificador: query("#SEX_Identificador").value,
        SEX_Estado: '1',
    };

    Edit(codigo, data);
});

load_on(document, 'click', '.btn-filter-guardar', function(e) {
    let id = query('.general-id') != null ? query('.general-id').getAttribute("data-id") : 0;
    let data = {
        SEX_Codigo: id,
        SEX_Nombre: "",
        SEX_Identificador: "",
        SEX_Estado: '0',
    };

    Delete(id, data);
});

load_on(document, 'click', '.btn-crud-Agregar', function(e) {
    let data = {
        SEX_Codigo: query("#Bus_SEX_Codigo").value == "" ? "%%" : query("#Bus_SEX_Codigo").value,
        SEX_Nombre: query("#Bus_SEX_Nombre").value == "" ? "%%" : query("#Bus_SEX_Nombre").value,
        SEX_Identificador: query("#Bus_SEX_Identificador").value == "" ? "%%" : query("#Bus_SEX_Identificador").value,
        SEX_Estado: query("#Bus_SEX_Estado").value == "" ? "%%" : query("#Bus_SEX_Estado").value,
    };

    Buscar(data);
});
