import { query, queryAll, load_on } from "../../../load.min.js";
import { Create, Edit, Delete, Buscar, obtenerHora } from "../axios.min.js";



load_on(document, 'click', '.btn-agregar-modal', function(e) {
    let data = {
        SUC_CodigoInterno: 0,
        DIS_CodigoInterno: query("#DIS_CodigoInterno").getAttribute("data-id"),
        SUC_Referencia: query("#SUC_Referencia").value,
        SUC_Direccion: query("#SUC_Direccion").value,
        SUC_HorasAtencion: obtenerHora(),
        SUC_Telefono: query("#SUC_Telefono").value,
        SUC_URL: query("#SUC_URL").value,
        SUC_Estado: "1",
    };


    Create(data);
});


load_on(document, 'click', '.btn-crud-edit', function(e) {
    let codigo = query('#CodigoInterno') != "" ? query('#CodigoInterno').value : 0;

    let data = {
        SUC_CodigoInterno: codigo,
        DIS_CodigoInterno: query("#DIS_CodigoInterno").getAttribute("data-id"),
        SUC_Referencia: query("#SUC_Referencia").value,
        SUC_Direccion: query("#SUC_Direccion").value,
        SUC_HorasAtencion: obtenerHora(),
        SUC_Telefono: query("#SUC_Telefono").value,
        SUC_URL: query("#SUC_URL").value,
        SUC_Estado: "1",
    };

    Edit(codigo, data);
});

load_on(document, 'click', '.btn-filter-guardar', function(e) {
    let id = query('.general-id') != null ? query('.general-id').getAttribute("data-id") : 0;
    let data = {
        SUC_CodigoInterno: id,
        DIS_CodigoInterno: '',
        SUC_Referencia: '',
        SUC_Direccion: '',
        SUC_HorasAtencion: '',
        SUC_Telefono: '',
        SUC_URL: '',
        SUC_Estado: "1",
    };

    Delete(id, data);
});

load_on(document, 'click', '.btn-crud-Agregar', function(e) {
    let data = {
        SUC_CodigoInterno: query("#Bus_SUC_CodigoInterno").value == "" ? "%%" : query("#Bus_SUC_CodigoInterno").value,
        DIS_CodigoInterno: query("#Bus_DIS_Index").value == "" ? "%%" : query("#Bus_DIS_Index").value,
        SUC_Referencia: query("#Bus_SUC_Referencia").value == "" ? "%%" : query("#Bus_SUC_Referencia").value,
        SUC_HorasAtencion: query("#Bus_SUC_HorasAtencion").value == "" ? "%%" : query("#Bus_SUC_HorasAtencion").value,
        SUC_Estado: query("#Bus_SUC_Estado").value == "" ? "%%" : query("#Bus_SUC_Estado").value,
    };

    Buscar(data);
});