import { query, queryAll, load_on } from "../../../load.min.js";
import { Create, Edit, Delete, Buscar } from "../axios.min.js";

load_on(document, 'click', '.btn-agregar-modal', function(e) {
    let data = {
        TIPART_Codigo: 0,
        TIPART_Nombre: query("#TIPART_Nombre").value,
        TIPART_Estado: '1',
    };
    Create(data);
});

load_on(document, 'click', '.btn-crud-edit', function(e) {
    let codigo = query('#CodigoInterno') != "" ? query('#CodigoInterno').value : 0;
    let data = {
        TIPART_Codigo: codigo,
        TIPART_Nombre: query("#TIPART_Nombre").value,
        TIPART_Estado: '1',
    };

    Edit(codigo, data);
});

load_on(document, 'click', '.btn-filter-guardar', function(e) {
    let id = query('.general-id') != null ? query('.general-id').getAttribute("data-id") : 0;
    let data = {
        TIPART_Codigo: id,
        TIPART_Nombre: "",
        TIPART_Estado: '0',
    };
    Delete(id, data);
});

load_on(document, 'click', '.btn-crud-Agregar', function(e) {
    let data = {
        TIPART_Codigo: query("#Bus_TIPART_Codigo").value == "" ? "%%" : query("#Bus_TIPART_Codigo").value,
        TIPART_Nombre: query("#Bus_TIPART_Nombre").value == "" ? "%%" : query("#Bus_TIPART_Nombre").value,
        TIPART_Estado: query("#Bus_TIPART_Estado").value == "" ? "%%" : query("#Bus_TIPART_Estado").value,
    };
    Buscar(data);
});
