import { query, queryAll, load_on } from "../../../load.min.js";
import { Create, Edit, Delete, Buscar } from "../axios.min.js";

load_on(document, 'click', '.btn-agregar-modal', function(e) {
    let data = {
        TIPPED_Codigo: 0,
        TIPPED_Nombre: query("#TIPPED_Nombre").value,
        TIPPED_Estado: '1',
    };
    Create(data);
});

load_on(document, 'click', '.btn-crud-edit', function(e) {
    let codigo = query('#CodigoInterno') != "" ? query('#CodigoInterno').value : 0;

    let data = {
        TIPPED_Codigo: codigo,
        TIPPED_Nombre: query("#TIPPED_Nombre").value,
        TIPPED_Estado: '1',
    };

    Edit(codigo, data);
});

load_on(document, 'click', '.btn-filter-guardar', function(e) {
    let id = query('.general-id') != null ? query('.general-id').getAttribute("data-id") : 0;
    let data = {
        TIPPED_Codigo: id,
        TIPPED_Nombre: "",
        TIPPED_Estado: '0',
    };

    Delete(id, data);
});

load_on(document, 'click', '.btn-crud-Agregar', function(e) {
    let data = {
        TIPPED_Codigo: query("#Bus_TIPPED_Codigo").value == "" ? "%%" : query("#Bus_TIPPED_Codigo").value,
        TIPPED_Nombre: query("#Bus_TIPPED_Nombre").value == "" ? "%%" : query("#Bus_TIPPED_Nombre").value,
        TIPPED_Estado: query("#Bus_TIPPED_Estado").value == "" ? "%%" : query("#Bus_TIPPED_Estado").value,
    };
    Buscar(data);
});
