import { query, queryAll, load_on } from "../../../load.min.js";
import { Create, Edit, Delete, Buscar } from "../axios.min.js";

load_on(document, 'click', '.btn-agregar-modal', function(e) {
    let data = {
        UBI_CodigoInterno: 0,
        LUG_CodigoInterno: query("#LUG_CodigoInterno").value,
        PROV_CodigoInterno: query("#PROV_CodigoInterno").value,
        DIS_CodigoInterno: query("#DIS_CodigoInterno").value,
        UBI_Direccion: query("#UBI_Direccion").value,
        UBI_Calle: query("#UBI_Calle").value,
        UBI_Numero: query("#UBI_Numero").value,
        UBI_Referencia: query("#UBI_Referencia").value,
        USU_CodigoInterno: query("#USU_CodigoInterno").value,
        UBI_Estado: "1",
    };


    Create(data);
});


load_on(document, 'click', '.btn-crud-edit', function(e) {
    let codigo = query('#CodigoInterno') != "" ? query('#CodigoInterno').value : 0;

    let data = {
        UBI_CodigoInterno: codigo,
        LUG_CodigoInterno: query("#LUG_CodigoInterno").value,
        PROV_CodigoInterno: query("#PROV_CodigoInterno").value,
        DIS_CodigoInterno: query("#DIS_CodigoInterno").value,
        UBI_Direccion: query("#UBI_Direccion").value,
        UBI_Calle: query("#UBI_Calle").value,
        UBI_Numero: query("#UBI_Numero").value,
        UBI_Referencia: query("#UBI_Referencia").value,
        USU_CodigoInterno: query("#USU_CodigoInterno").value,
        UBI_Estado: "1",
    };

    Edit(codigo, data);
});

load_on(document, 'click', '.btn-filter-guardar', function(e) {
    let id = query('.general-id') != null ? query('.general-id').getAttribute("data-id") : 0;
    let data = {
        UBI_CodigoInterno: id,
        LUG_CodigoInterno: "",
        PROV_CodigoInterno: "",
        DIS_CodigoInterno: "",
        UBI_Direccion: "",
        UBI_Calle: "",
        UBI_Numero: "",
        UBI_Referencia: "",
        USU_CodigoInterno: "",
        UBI_Estado: "1",
    };

    Delete(id, data);
});

load_on(document, 'click', '.btn-crud-Agregar', function(e) {
    let data = {
        UBI_CodigoInterno: query("#Bus_UBI_CodigoInterno").value == "" ? "%%" : query("#Bus_UBI_CodigoInterno").value,
        LUG_CodigoInterno: query("#Bus_LUG_CodigoInterno").value == "" ? "%%" : query("#Bus_LUG_CodigoInterno").value,
        PROV_CodigoInterno: query("#Bus_PROV_CodigoInterno").value == "" ? "%%" : query("#Bus_PROV_CodigoInterno").value,
        DIS_CodigoInterno: query("#Bus_DIS_CodigoInterno").value == "" ? "%%" : query("#Bus_DIS_CodigoInterno").value,
        UBI_Direccion: query("#Bus_UBI_Direccion").value == "" ? "%%" : query("#Bus_UBI_Direccion").value,
        UBI_Calle: query("#Bus_UBI_Calle").value == "" ? "%%" : query("#Bus_UBI_Calle").value,
        UBI_Numero: query("#Bus_UBI_Numero").value == "" ? "%%" : query("#Bus_UBI_Numero").value,
        UBI_Referencia: query("#Bus_UBI_Referencia").value == "" ? "%%" : query("#Bus_UBI_Referencia").value,
        USU_CodigoInterno: query("#Bus_USU_CodigoInterno").value == "" ? "%%" : query("#Bus_USU_CodigoInterno").value,
        UBI_Estado: query("#Bus_UBI_Estado").value == "" ? "%%" : query("#Bus_UBI_Estado").value,
    };

    Buscar(data);
});
